function setup(){for(var a=0;50>a;a++)for(var b=0;50>b;b++)$(".board").append('<div class="quadrant" data-xcoord="'+b+'" data-ycoord="'+a+'"></div>')}function isOver(a,b){return a>=50||b>=50||0>a||0>b?!0:!1}function progress_snake(a,b){$("[data-xcoord ="+a+"][data-ycoord ="+b+"]").addClass("litup");var c=setTimeout(function(){switch($("[data-xcoord ="+a+"][data-ycoord ="+b+"]").removeClass("litup"),direction){case"left":a--;break;case"up":b--;break;case"right":a++;break;case"down":b++;break;default:return}isOver(a,b)?(clearTimeout(c),console.log("Over!")):progress_snake(a,b)},100)}var direction="right";$(document).ready(function(){setup(),progress_snake(0,0)}),$(document).keydown(function(a){switch(a.which){case 37:direction="left",console.log("left");break;case 38:direction="up",console.log("up");break;case 39:direction="right",console.log("right");break;case 40:direction="down",console.log("down");break;default:return}a.preventDefault()});